<?phpnamespace App\Http\Middleware;use Closure;class ValidarEnvioConvite{    /**     * Handle an incoming request.     *     * @param  \Illuminate\Http\Request  $request     * @param  \Closure  $next     * @return mixed     */    public function handle($request, Closure $next)    {        return app(\App\Http\Middleware\VerificarExistenciaPromotor::class)->handle($request, function($request) use($next) {            $all=$request->json()->all();            If(isset($all['viewName'])&& isset($all['viewData'])){                $request->{'view'}=$all['viewName'];                $request->{'viewData'}=$all['viewData'];                return $next($request);            }else{                return abort(404,"Nome da view ou view Data nao encontados");            }        });    }}